 .data(imageUrl)
 .crossfade(true)
 .build(),
 contentDescription = "Event image for $title",
 contentScale = ContentScale.Crop,
 modifier = Modifier.fillMaxSize(),
 placeholder = painterResource(id = R.drawable.ic_location),
